// Find the greatest product of five consecutive digits in the 1000-digit number.

public class Product {

	public static void main(String[] args){
		// 1000 digit number "number"
		String numberString = "";
		long tempProduct = 1;
		long product = 1;
		int x1=0,x2=0,x3=0,x4=0,x5=0;
		
		x1 = Character.getNumericValue(numberString.charAt(0));
		x2 = Character.getNumericValue(numberString.charAt(1));
		x3 = Character.getNumericValue(numberString.charAt(2));
		x4 = Character.getNumericValue(numberString.charAt(3));
		x5 = Character.getNumericValue(numberString.charAt(4));
		product = x1*x2*x3*x4*x5;
		
		for(int i=5;i<numberString.length();i++){
			
			
			x1 = Character.getNumericValue(numberString.charAt(i));
			x2 = Character.getNumericValue(numberString.charAt(i-1));
			x3 = Character.getNumericValue(numberString.charAt(i-2));
			x4 = Character.getNumericValue(numberString.charAt(i-3));
			x5 = Character.getNumericValue(numberString.charAt(i-4));
			
			tempProduct = x1*x2*x3*x4*x5;
			if(tempProduct > product){
				product = tempProduct;
			}
			
			
		}
		System.out.println(product);
	}
}
